con:configuration.scriptText:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

districts = groovyUtils.getXmlHolder("Response")
zones = groovyUtils.getXmlHolder("getZonesInMission#Response")

// get a list of zoneIds from the getZonesInMission call
zoneIds = zones["//zoneId"]
zoneIds.each {
	log.info "zoneId: " + it
}	

// for each district ensure the zoneID is in the list of Zones
districts["//zoneId"].each {
  log.info "district zoneId: " + it
  assert it in zoneIds
}

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

proselytingAreas = groovyUtils.getXmlHolder("Response")
districts = groovyUtils.getXmlHolder("getDistrictsInMission#Response")

// get district names from getDistrictsInMission call
districtNames = districts["//districtName"]
districtNames.each {
	log.info "district name: " + it
}	

// each proselyting area district name must a valid district name
proselytingAreas["//districtName"].each {
  log.info "proselyting area district name: " + it
  assert it in districtNames
}

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)

missionaryDetails = groovyUtils.getXmlHolder("Response")
missionaryAssignments = groovyUtils.getXmlHolder("getAssignmentsByMission#Response")

// get missionaries assigned in mission
individualIds = missionaryAssignments["//individualId"]
individualIds.each {
	log.info "assignments individualId: " + it
}	

// missionaries in mission should also be in assignment list  
missionaryDetails["//individualId"].each {
  log.info "details individualId: " + it
  assert it in individualIds
}

