con:testSuite.con:tearDownScript:
import groovy.sql.Sql
import com.eviware.soapui.model.testsuite.*

	// disabled testcases are considered blocked
def blocks = testSuite.getTestCaseList().findAll{it.isDisabled()}.size()
def failures = 0
def passes = 0

	// loop through each testcase checking for success/failure
runner.getResults().each
{
		// If any test steps have a failure (!OK) consider the testcase a failure
	if ( it.getResults().findAll{ it.getStatus() != TestStepResult.TestStepStatus.OK }.size() > 0 )
		failures++
	else
		passes++
}	

log.info "Results: " 
log.info " blocked: " + blocks 
log.info " failures: " + failures 
log.info " passes: " + passes

def sql = Sql.newInstance("jdbc:oracle:thin:@10.118.201.80:1521:T002","dashboard","m3real","oracle.jdbc.driver.OracleDriver")
def summary = sql.dataSet('TESTSUMMARY')
def id = sql.firstRow("SELECT itfqa.TESTSUMMARY_SEQ.NEXTVAL FROM TESTSUMMARY").nextval
summary.add(id:id,block:blocks,fail:failures,pass:passes,suitename:"Missionary - SOAP",project:"Community Services",run_date:new java.sql.Date(System.currentTimeMillis()),environment:"QA")

